extension_params.template:
{% for address_id,address in record_requesters %}
<div class="bubble">
	{{cerb_avatar_image('address',address_id,0)}}
	<a href="javascript:;" class="cerb-peek-trigger no-underline" data-context="address" data-context-id="{{address_id}}">{{address.email}}</a>
</div>
{% endfor %}

<script type="text/javascript">
$(function() {
	var $widget = $('#profileWidget{{widget_id}}');
	
	$widget.find('.cerb-peek-trigger')
		.cerbPeekTrigger()
		;
});
</script>
