card_templates.cerberusweb.contexts.task:
{% spaceless %}
{% if is_completed %}

<div style="font-size:120%;">
	<span class="glyphicons glyphicons-circle-ok" style="color:rgb(0,150,0);"></span> Completed!
</div>

{% else %}

{% if custom_64 is not empty and custom_65 is not empty %}

{% if column__label == 'In Progress' %}

<button type="button" class="cerb-bot-trigger" data-behavior-id="{{custom_64}}" data-interaction="{{custom_65}}">{{cerb_avatar_image('app', 0, 0)}} Start Lesson</button>

{% elseif column__label == 'TODO' %}

<div>
	Drag this to <b>In Progress</b> to start <span class="glyphicons glyphicons-right-arrow"></span>
</div>

{% endif %}
{% endif %}
{% endif %}
{% endspaceless %}
